import{_ as e,c as a,o as r,a as s}from"./app.eda6f95c.js";const _='{"title":"\u5982\u4F55\u5728Mac\u4E2DHook:\u6539\u9020Rest\u4EE5\u9002\u5E94\u5DE5\u4F5C\u9700\u6C42","description":"","frontmatter":{"title":"\u5982\u4F55\u5728Mac\u4E2DHook:\u6539\u9020Rest\u4EE5\u9002\u5E94\u5DE5\u4F5C\u9700\u6C42","date":"2022-02-16T13:57:03.000Z","draft":false,"tags":["security","mac","objectc"],"categories":["\u6280\u672F\u76F8\u5173"]},"headers":[{"level":2,"title":"\u80CC\u666F","slug":"\u80CC\u666F"},{"level":2,"title":"\u5DE5\u5177\u6536\u96C6","slug":"\u5DE5\u5177\u6536\u96C6"},{"level":3,"title":"1. \u5982\u4F55\u6CE8\u5165\u4E00\u4E2Adylib","slug":"_1-\u5982\u4F55\u6CE8\u5165\u4E00\u4E2Adylib"},{"level":3,"title":"2. \u5982\u4F55\u8FDB\u884Chook","slug":"_2-\u5982\u4F55\u8FDB\u884Chook"},{"level":2,"title":"\u6CE8\u5165\u70B9\u5206\u6790","slug":"\u6CE8\u5165\u70B9\u5206\u6790"},{"level":2,"title":"\u6CE8\u5165","slug":"\u6CE8\u5165"},{"level":2,"title":"\u5176\u4ED6","slug":"\u5176\u4ED6"}],"relativePath":"app_security/hook_rest_for_work.md"}',n={},l=s(`<h2 id="\u80CC\u666F" tabindex="-1">\u80CC\u666F <a class="header-anchor" href="#\u80CC\u666F" aria-hidden="true">#</a></h2><p>\u5148\u8BF4\u4E00\u4E0B\u80CC\u666F,Rest\u662F\u4E00\u4E2A\u5728Mac\u4E0A\u7528\u7684\u4F11\u606F\u63D0\u9192\u5DE5\u5177,\u53EF\u4EE5\u5728AppStore\u4E2D\u8D2D\u4E70(<strong>\u4E0D\u514D\u8D39</strong>). \u672C\u6765\u4E00\u76F4\u7528\u7684\u597D\u597D\u7684,\u4F46\u662F\u73B0\u5728\u516C\u53F8\u8981\u6C42\u79BB\u5F00\u5DE5\u4F4D\u5FC5\u987B\u9501\u5C4F,\u8FD9\u5C31\u5BFC\u81F4\u6211\u5728\u4F11\u606F\u7684\u65F6\u5019\u53EA\u80FD\u5728\u5DE5\u4F4D\u8FB9\u4E0A,\u5426\u5219\u4E0A\u4E2A\u5395\u6240\u56DE\u6765,\u4F11\u606F\u7ED3\u675F\u4E86,\u684C\u9762\u5C31\u76F4\u63A5\u66B4\u9732\u51FA\u6765\u4E86. \u7B80\u5355\u770B\u4E86\u4E00\u4E0BRest\u662F\u7528ObjectC\u7F16\u5199\u7684,\u57FA\u672C\u6EE1\u8DB3\u6211\u7684\u8981\u6C42,\u4E3B\u8981\u6709\u4EE5\u4E0B\u95EE\u9898:</p><ol><li>\u4F11\u606F\u7684\u65F6\u5019,\u5FC5\u987B\u94A9\u4E0A<code>Prevent me from working during beaks</code>,\u5426\u5219\u5982\u679C\u6709\u591A\u4E2A\u865A\u62DF\u684C\u9762,\u5C31\u4F1A\u5BFC\u81F4\u66B4\u9732\u684C\u9762\u5185\u5BB9.</li><li>\u4F11\u606F\u7ED3\u675F\u540E\u4E0D\u80FD\u81EA\u52A8\u9501\u5C4F</li><li>\u9488\u5BF9\u95EE\u98981,\u5982\u679C\u5F00\u542F\u4E86<code>Prevent me from working during beaks</code>,\u5728\u4F11\u606F\u65F6,\u5982\u679C\u4E07\u4E00\u6709\u5E94\u6025\u5DE5\u4F5C,\u5C31\u4E0D\u80FD\u7ACB\u5373\u54CD\u5E94\u4E86.</li></ol><h2 id="\u5DE5\u5177\u6536\u96C6" tabindex="-1">\u5DE5\u5177\u6536\u96C6 <a class="header-anchor" href="#\u5DE5\u5177\u6536\u96C6" aria-hidden="true">#</a></h2><h3 id="_1-\u5982\u4F55\u6CE8\u5165\u4E00\u4E2Adylib" tabindex="-1">1. \u5982\u4F55\u6CE8\u5165\u4E00\u4E2Adylib <a class="header-anchor" href="#_1-\u5982\u4F55\u6CE8\u5165\u4E00\u4E2Adylib" aria-hidden="true">#</a></h3><p>\u4E3B\u8981\u6709\u4E24\u79CD\u65B9\u5F0F,\u4E00\u79CD\u662F\u52A8\u6001\u6CE8\u5165,\u4E00\u79CD\u662F\u6301\u4E45\u6CE8\u5165. \u5982\u679C\u662F\u52A8\u6001\u6CE8\u5165,\u56E0\u4E3A\u4E0D\u9700\u8981\u8FDC\u7A0B\u6CE8\u5165,\u6240\u4EE5\u5F88\u7B80\u5355\u7684,\u901A\u8FC7DYLD_INSERT_LIBRARIES\u542F\u52A8\u5373\u53EF,\u5177\u4F53\u6765\u8BF4:</p><div class="language-bash line-numbers-mode"><pre><code><span class="token assign-left variable">DYLD_INSERT_LIBRARIES</span><span class="token operator">=</span>libmyinjectlib.dylib ./Res
</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br></div></div><p>\u53E6\u4E00\u79CD\u5C31\u9EBB\u70E6\u4E00\u70B9,\u53EF\u4EE5\u901A\u8FC7\u7C7B\u4F3C<a href="https://github.com/gengjf/insert_dylib" target="_blank" rel="noopener noreferrer">inject_dylib</a>\u8FD9\u6837\u7684\u5DE5\u5177\u9759\u6001\u6CE8\u5165, \u4F46\u662F\u6211\u5C1D\u8BD5\u540E\u53D1\u73B0\u4FEE\u6539\u540E\u7684\u7A0B\u5E8F\u65E0\u6CD5\u8FD0\u884C,\u6240\u4EE5\u6682\u7528\u52A8\u6001\u6CE8\u5165\u7684\u65B9\u5F0F,\u8FD9\u79CD\u65B9\u5F0F\u81F3\u5C11\u53EF\u4EE5\u6EE1\u8DB3\u6211\u7684\u8981\u6C42,\u53EA\u9700\u8981\u4E00\u4E2A\u542F\u52A8\u811A\u672C\u5373\u53EF.</p><h3 id="_2-\u5982\u4F55\u8FDB\u884Chook" tabindex="-1">2. \u5982\u4F55\u8FDB\u884Chook <a class="header-anchor" href="#_2-\u5982\u4F55\u8FDB\u884Chook" aria-hidden="true">#</a></h3><p>\u8FD9\u4E2A\u8981\u4F9D\u8D56\u7684\u5DE5\u5177\u5C31\u662F<a href="https://github.com/rpetrich/CaptainHook.git" target="_blank" rel="noopener noreferrer">CaptainHook</a>,\u53EA\u6709\u4E00\u4E2A\u5934\u6587\u4EF6,\u7528\u8D77\u6765\u4E5F\u6BD4\u8F83\u7B80\u5355.</p><h2 id="\u6CE8\u5165\u70B9\u5206\u6790" tabindex="-1">\u6CE8\u5165\u70B9\u5206\u6790 <a class="header-anchor" href="#\u6CE8\u5165\u70B9\u5206\u6790" aria-hidden="true">#</a></h2><p>\u4F11\u606F\u7A97\u53E3\u7684\u7279\u70B9\u662F\u4E00\u4E2A\u5168\u5C4F\u7684\u7A97\u53E3,\u5E76\u4E14\u8BE5\u7A97\u53E3\u8981\u5728\u6240\u6709\u7A97\u53E3\u4E4B\u524D. \u56E0\u6B64NSWindow\u7684setLevel\u5C31\u662F\u4E00\u4E2A\u6BD4\u8F83\u597D\u7684\u5165\u53E3\u70B9,\u7ECF\u8FC7\u641C\u7D22\u53D1\u73B0,\u8BE5\u8C03\u7528\u53EA\u6709\u4E00\u5904<code>-[AppDelegate setBreakScreens](AppDelegate *self, SEL a2)</code>. \u7ECF\u8FC7\u5BF9\u8BE5\u51FD\u6570\u7684\u5206\u6790,\u53EF\u4EE5\u53D1\u73B0\u8FD9\u4E2A\u5C31\u662F\u542F\u52A8\u4F11\u606F\u7684\u51FD\u6570.</p><p>\u6CE8\u610F\u5230\u91CC\u9762\u7684<code>removeBreakScreens</code>,\u6309\u7167\u540D\u5B57\u63D0\u793A,\u5E94\u8BE5\u662F\u7ED3\u675F\u4F11\u606F\u7684\u51FD\u6570. \u6240\u4EE5\u5BF9\u4EE5\u4E0A\u4E24\u4E2A\u51FD\u6570\u8FDB\u884Chook,\u5E94\u8BE5\u53EF\u4EE5\u8FBE\u5230\u6211\u4EEC\u7684\u76EE\u7684:</p><ol><li>hook <code>removeBreakScreens</code>,\u7ED3\u675F\u7684\u65F6\u5019\u8FDB\u884C\u9501\u5C4F.</li><li>hook <code>setBreakScreens</code> \u53EF\u4EE5\u5728\u91CC\u9762\u6CE8\u518C\u4E00\u4E2A\u5FEB\u6377\u952E(\u4E0D\u662F\u5168\u5C40\u7684),\u8FD9\u6837\u4E07\u4E00\u6709\u5DE5\u4F5C\u7684\u65F6\u5019,\u53EF\u4EE5\u7ACB\u5373\u7ED3\u675F\u4F11\u606F.</li></ol><h2 id="\u6CE8\u5165" tabindex="-1">\u6CE8\u5165 <a class="header-anchor" href="#\u6CE8\u5165" aria-hidden="true">#</a></h2><p>\u6709\u4E86\u6CE8\u5165\u70B9,\u5269\u4E0B\u5C31\u662F\u7528CaptainHook\u8FDB\u884C\u6CE8\u5165\u4E86.</p><div class="language-objectc line-numbers-mode"><pre><code>CHDeclareClass(AppDelegate)
CHOptimizedMethod0(self, void, AppDelegate, removeBreakScreens) {
    NSLog(@&quot;removeBreakScreens called&quot;);
    CHSuper0(AppDelegate, removeBreakScreens);
}

CHOptimizedMethod0(self, void, AppDelegate, setBreakScreens) {
    NSLog(@&quot;setBreakScreens called&quot;);
    CHSuper0(AppDelegate, setBreakScreens);
}

CHConstructor {
    CHLoadLateClass(AppDelegate);
    CHClassHook0(AppDelegate, removeBreakScreens);
    CHClassHook0(AppDelegate, setBreakScreens);
};

</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br></div></div><p>\u7ECF\u8FC7\u9A8C\u8BC1,\u786E\u5B9E\u6BCF\u6B21\u542F\u52A8\u4F11\u606F\u90FD\u4F1A\u8C03\u7528<code>setBreakScreens</code>,\u800C\u65E0\u8BBA\u662F\u6B63\u5E38\u7ED3\u675F\u4F11\u606F,\u8FD8\u662F\u70B9\u51FB<code>let me work</code>\u90FD\u4F1A\u8C03\u7528<code>removeBreakScreens</code>.</p><h2 id="\u5176\u4ED6" tabindex="-1">\u5176\u4ED6 <a class="header-anchor" href="#\u5176\u4ED6" aria-hidden="true">#</a></h2><p>\u8FD9\u662F\u9996\u6B21\u5728Mac\u4E0A\u6210\u529F\u4FEE\u6539\u4E00\u4E2A\u7A0B\u5E8F\u7684\u903B\u8F91,\u4EE5\u524D\u641E\u8FC7Windows\u548CLinux,Mac\u8FD8\u662F\u9996\u6B21,ObjectC\u548CC/C++\u6BD4\u8D77\u6765,\u8FD0\u884C\u65F6\u8981\u590D\u6742\u7684\u591A,\u7279\u522B\u9002\u5408\u6CE8\u5165. \u5B8C\u6574\u7684\u7A0B\u5E8F\u5DF2\u7ECF\u653E\u5728<a href="https://github.com/nkbai/objectc_practices" target="_blank" rel="noopener noreferrer">\u6211\u7684github</a>,\u6B22\u8FCE\u8BD5\u7528.\u5982\u679C\u4F60\u4EEC\u516C\u53F8\u4E5F\u6709\u9501\u5C4F\u8981\u6C42\u7684\u8BDD.</p>`,20),o=[l];function t(i,c,p,d,b,h){return r(),a("div",null,o)}var m=e(n,[["render",t]]);export{_ as __pageData,m as default};
