import{_ as n,c as a,o as s,a as e}from"./app.e7435feb.js";const y='{"title":"\u79FB\u52A8App\u52A0\u5BC6\u7684\u4E09\u6761\u519B\u89C4","description":"","frontmatter":{"title":"\u79FB\u52A8App\u52A0\u5BC6\u7684\u4E09\u6761\u519B\u89C4","date":"2022-01-17T13:57:03.000Z","hide":false,"tags":["security","app","android","cryptography"],"categories":["\u6280\u672F\u76F8\u5173"]},"headers":[{"level":2,"title":"\u4E3A\u4EC0\u4E48\u9700\u8981\u52A0\u5BC6","slug":"\u4E3A\u4EC0\u4E48\u9700\u8981\u52A0\u5BC6"},{"level":2,"title":"\u52A0\u5BC6\u9700\u8981\u9075\u5FAA\u7684\u4E09\u6761\u519B\u89C4","slug":"\u52A0\u5BC6\u9700\u8981\u9075\u5FAA\u7684\u4E09\u6761\u519B\u89C4"},{"level":3,"title":"1. \u4E0D\u8981\u786C\u7F16\u7801\u79D8\u94A5","slug":"_1-\u4E0D\u8981\u786C\u7F16\u7801\u79D8\u94A5"},{"level":3,"title":"2. \u53EF\u9884\u6D4B\u7684\u52A0\u5BC6\u79D8\u94A5","slug":"_2-\u53EF\u9884\u6D4B\u7684\u52A0\u5BC6\u79D8\u94A5"},{"level":3,"title":"3. \u4F7F\u7528\u7CFB\u7EDF\u63D0\u4F9B\u52A0\u5BC6\u65B9\u6CD5","slug":"_3-\u4F7F\u7528\u7CFB\u7EDF\u63D0\u4F9B\u52A0\u5BC6\u65B9\u6CD5"},{"level":2,"title":"\u5176\u4ED6","slug":"\u5176\u4ED6"}],"relativePath":"app_security/use_cryptography_in_mobile_appls_the_right_way.md"}',p={},t=e(`<p>\u672C\u6587\u8BD1\u81EA<a href="https://blog.oversecured.com/Use-cryptography-in-mobile-apps-the-right-way/" target="_blank" rel="noopener noreferrer">Use cryptography in mobile apps the right way</a></p><p>\u52A0\u5BC6\u662F\u4FDD\u62A4\u6570\u636E\u7684\u91CD\u8981\u624B\u6BB5,\u4F46\u662F\u4E0D\u5C11\u5F00\u53D1\u8005\u7ECF\u5E38\u8BEF\u7528\u52A0\u5BC6\u63AA\u65BD.</p><h2 id="\u4E3A\u4EC0\u4E48\u9700\u8981\u52A0\u5BC6" tabindex="-1">\u4E3A\u4EC0\u4E48\u9700\u8981\u52A0\u5BC6 <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u9700\u8981\u52A0\u5BC6" aria-hidden="true">#</a></h2><p>\u867D\u7136\u5B89\u5353\u548Cios\u90FD\u63D0\u4F9B\u4E86\u6C99\u76D2\u673A\u5236\u6765\u9694\u79BB\u4E0D\u540CApp\u4E4B\u95F4\u7684\u8FD0\u884C\u73AF\u5883,\u4F46\u662F\u5BF9\u4E8E\u654F\u611F\u6570\u636E,\u8FD8\u662F\u5FC5\u987B\u8FDB\u884C\u4FDD\u62A4. \u81F3\u5C11\u5E94\u8BE5\u8FDB\u884C\u76D1\u7BA1\u8981\u6C42\u7684\u6700\u4F4E\u7EBF\u4FDD\u62A4. \u53E6\u5916,\u56E0\u4E3AApp\u4E2D\u53EF\u80FD\u5B58\u5728\u7684\u5404\u79CD\u6CC4\u9732App\u5185\u90E8\u6587\u4EF6\u7684\u5B89\u5168\u6F0F\u6D1E:</p><ul><li>\u901A\u8FC7WebView\u6CC4\u9732</li><li>\u901A\u8FC7contentProviders\u6CC4\u9732</li><li>\u901A\u8FC7\u9690\u5F0F\u7684intent(implicit intents)</li><li>\u5176\u4ED6\u65B9\u5F0F</li></ul><p>\u52A0\u5BC6\u53EF\u4EE5\u6709\u6548\u4FDD\u8BC1,\u5373\u4F7F\u5B58\u5728\u8FD9\u4E9B\u95EE\u9898,\u653B\u51FB\u8005\u4E5F\u56E0\u4E3A\u52A0\u5BC6\u624B\u6BB5\u7684\u5B58\u5728\u800C\u5F88\u96BE\u83B7\u53D6\u5230\u4EC0\u4E48\u597D\u5904. \u53EF\u4EE5\u8BFB\u53D6\u5185\u90E8\u6587\u4EF6\u7684\u6F0F\u6D1E,\u53EA\u4F1A\u5BFC\u81F4\u76F8\u5173\u6587\u4EF6\u5185\u5BB9\u6CC4\u9732,\u800C\u4E0D\u4F1A\u5BFC\u81F4\u6574\u4E2A\u79D8\u94A5\u4EE5\u53CA\u5176\u4ED6\u6587\u4EF6\u6CC4\u9732. \u800C\u5199\u6587\u4EF6\u5219\u4F1A\u5BFC\u81F4\u7528\u6237\u8D26\u6237\u7684\u81EA\u52A8\u6CE8\u9500.</p><h2 id="\u52A0\u5BC6\u9700\u8981\u9075\u5FAA\u7684\u4E09\u6761\u519B\u89C4" tabindex="-1">\u52A0\u5BC6\u9700\u8981\u9075\u5FAA\u7684\u4E09\u6761\u519B\u89C4 <a class="header-anchor" href="#\u52A0\u5BC6\u9700\u8981\u9075\u5FAA\u7684\u4E09\u6761\u519B\u89C4" aria-hidden="true">#</a></h2><h3 id="_1-\u4E0D\u8981\u786C\u7F16\u7801\u79D8\u94A5" tabindex="-1">1. \u4E0D\u8981\u786C\u7F16\u7801\u79D8\u94A5 <a class="header-anchor" href="#_1-\u4E0D\u8981\u786C\u7F16\u7801\u79D8\u94A5" aria-hidden="true">#</a></h3><p>\u786C\u7F16\u7801\u79D8\u94A5\u610F\u5473\u7740\u53EF\u4EE5\u4ECE\u6E90\u7801\u4E2D\u76F4\u63A5\u63D0\u53D6\u79D8\u94A5,\u800C\u8FD9\u5176\u5B9E\u548C\u6CA1\u6709\u52A0\u5BC6\u662F\u6CA1\u6709\u533A\u522B\u7684. \u5B89\u5353\u4E2D\u7684\u9519\u8BEF\u793A\u4F8B: <img alt="" data-src="assets/cryptograph_hardcode_key_android.png" loading="lazy" class="lazy"> ios\u4E2D\u7684\u9519\u8BEF\u793A\u4F8B: <img alt="" data-src="assets/cryptograph_hardcode_key_ios.png" loading="lazy" class="lazy"></p><h3 id="_2-\u53EF\u9884\u6D4B\u7684\u52A0\u5BC6\u79D8\u94A5" tabindex="-1">2. \u53EF\u9884\u6D4B\u7684\u52A0\u5BC6\u79D8\u94A5 <a class="header-anchor" href="#_2-\u53EF\u9884\u6D4B\u7684\u52A0\u5BC6\u79D8\u94A5" aria-hidden="true">#</a></h3><p>\u79D8\u94A5\u867D\u7136\u4F7F\u7528\u4E86\u968F\u673A\u6570\u6765\u751F\u6210,\u4F46\u662F\u968F\u673A\u6570\u7684\u79CD\u5B50\u5374\u662F\u53EF\u9884\u6D4B\u7684,\u8FD9\u540C\u6837\u4F1A\u5BFC\u81F4\u79D8\u94A5\u5F88\u5BB9\u6613\u63A8\u6D4B\u51FA\u6765.</p><p>\u5E38\u89C1\u7684\u9519\u8BEF\u5C31\u662F\u4F7F\u7528\u56FA\u5B9A\u7684\u79CD\u5B50:</p><div class="language-java line-numbers-mode"><pre><code>    <span class="token class-name">SecureRandom</span> secureRandom <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">SecureRandom</span><span class="token punctuation">(</span><span class="token string">&quot;known_seed&quot;</span><span class="token punctuation">.</span><span class="token function">getBytes</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token class-name">KeyGenerator</span> generator <span class="token operator">=</span> <span class="token class-name">KeyGenerator</span><span class="token punctuation">.</span><span class="token function">getInstance</span><span class="token punctuation">(</span><span class="token string">&quot;AES&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
    generator<span class="token punctuation">.</span><span class="token function">init</span><span class="token punctuation">(</span><span class="token number">256</span><span class="token punctuation">,</span> secureRandom<span class="token punctuation">)</span><span class="token punctuation">;</span>
    <span class="token class-name">Key</span> key <span class="token operator">=</span> generator<span class="token punctuation">.</span><span class="token function">generateKey</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>\u6216\u8005\u4F7F\u7528\u4F2A\u968F\u673A\u6570:</p><div class="language-java line-numbers-mode"><pre><code>    <span class="token keyword">private</span> <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">generateKey</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span> key <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token number">128</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
        <span class="token class-name">Random</span> random <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Random</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">int</span> i <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span> i <span class="token operator">&lt;</span> <span class="token number">128</span><span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
            key<span class="token punctuation">[</span>i<span class="token punctuation">]</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token keyword">byte</span><span class="token punctuation">)</span> random<span class="token punctuation">.</span><span class="token function">nextInt</span><span class="token punctuation">(</span><span class="token number">256</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token punctuation">}</span>
        <span class="token keyword">return</span> key<span class="token punctuation">;</span>
    <span class="token punctuation">}</span>

    <span class="token keyword">private</span> <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">encrypt</span><span class="token punctuation">(</span><span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span> data<span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token class-name">SecretKeySpec</span> skeySpec <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">SecretKeySpec</span><span class="token punctuation">(</span><span class="token function">generateKey</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">,</span> <span class="token string">&quot;AES&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token comment">//...</span>
    <span class="token punctuation">}</span>
</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br></div></div><p>\u6216\u8005\u79D8\u94A5\u4F7F\u7528\u672A\u521D\u59CB\u5316\u7684\u9ED8\u8BA4\u503C,\u4E0D\u540C\u4E8Ec++\u8BED\u8A00,java\u4E2D\u6570\u7EC4\u672A\u521D\u59CB\u5316,\u9ED8\u8BA4\u503C\u662F0:</p><div class="language-java line-numbers-mode"><pre><code>    <span class="token keyword">private</span> <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span> <span class="token function">encrypt</span><span class="token punctuation">(</span><span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span> data<span class="token punctuation">)</span> <span class="token punctuation">{</span>
        <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span> key <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token number">128</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
        <span class="token class-name">SecretKeySpec</span> skeySpec <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">SecretKeySpec</span><span class="token punctuation">(</span>key<span class="token punctuation">,</span> <span class="token string">&quot;AES&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token comment">//...</span>
    <span class="token punctuation">}</span>
</code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><h3 id="_3-\u4F7F\u7528\u7CFB\u7EDF\u63D0\u4F9B\u52A0\u5BC6\u65B9\u6CD5" tabindex="-1">3. \u4F7F\u7528\u7CFB\u7EDF\u63D0\u4F9B\u52A0\u5BC6\u65B9\u6CD5 <a class="header-anchor" href="#_3-\u4F7F\u7528\u7CFB\u7EDF\u63D0\u4F9B\u52A0\u5BC6\u65B9\u6CD5" aria-hidden="true">#</a></h3><h4 id="\u5B89\u5353\u8BF7\u7528keystore" tabindex="-1">\u5B89\u5353\u8BF7\u7528keystore <a class="header-anchor" href="#\u5B89\u5353\u8BF7\u7528keystore" aria-hidden="true">#</a></h4><p>\u5B89\u5353\u63D0\u4F9B\u4E86<a href="https://developer.android.com/training/articles/keystore" target="_blank" rel="noopener noreferrer">KeyStore</a>, App\u53EA\u80FD\u901A\u8FC7API\u6765\u83B7\u53D6\u79D8\u94A5,\u4E0D\u80FD\u76F4\u63A5\u8BFB\u53D6\u76F8\u5173\u6587\u4EF6. \u76F8\u5173\u79D8\u94A5\u5B58\u50A8\u5728<code>/data/misc/keystore</code>. \u666E\u901A\u7528\u6237\u65E0\u6CD5\u76F4\u63A5\u8BFB\u53D6. \u5F53\u7136\u5982\u679C\u624B\u673Aroot\u4E86,\u662F\u53EF\u4EE5\u8BFB\u53D6\u7684, \u8FD9\u4E5F\u8BC1\u660E\u4E86\u5BF9\u624B\u673Aroot\u7684\u5371\u5BB3.</p><h4 id="ios\u8BF7\u7528\u5B89\u5168\u9694\u79BB\u533A-secure-enclave" tabindex="-1">ios\u8BF7\u7528\u5B89\u5168\u9694\u79BB\u533A(<code>Secure Enclave</code>) <a class="header-anchor" href="#ios\u8BF7\u7528\u5B89\u5168\u9694\u79BB\u533A-secure-enclave" aria-hidden="true">#</a></h4><p>Secure Enclave\u662F\u82F9\u679C\u63D0\u4F9B\u7684\u5B58\u50A8\u79D8\u94A5\u4EE5\u53CA\u751F\u7269\u4FE1\u606F\u7684\u624B\u6BB5,\u5B83\u662F\u96C6\u6210\u5230SoC\u7684\u4E13\u7528\u5B89\u5168\u5B50\u7CFB\u7EDF,\u72EC\u7ACB\u4E8E\u5904\u7406\u5668. \u5373\u4F7F\u5904\u7406\u5668\u906D\u5230\u5165\u4FB5,\u4E5F\u53EF\u4EE5\u4FDD\u62A4\u654F\u611F\u6570\u636E\u7684\u5B89\u5168. \u5173\u4E8E<a href="https://support.apple.com/zh-cn/guide/security/sec59b0b31ff/web" target="_blank" rel="noopener noreferrer">\u5B89\u5168\u9694\u79BB\u533A</a>\u7684\u4ECB\u7ECD,\u53EF\u4EE5\u53C2\u8003\u82F9\u679C\u5B98\u7F51.</p><h2 id="\u5176\u4ED6" tabindex="-1">\u5176\u4ED6 <a class="header-anchor" href="#\u5176\u4ED6" aria-hidden="true">#</a></h2><p>\u5F53\u7136\u5C31\u7B97\u4F60\u4F7F\u7528\u4E86\u9075\u5FAA\u4E86\u4E0A\u8FF0\u89C4\u5219,\u4E5F\u4E0D\u80FD\u4FDD\u8BC1\u4F60\u7684\u6570\u636E100%\u5B89\u5168. \u5982\u679C\u4F60\u7684App\u5B58\u5728ACE(arbitrary code execution)\u95EE\u9898,\u90A3\u4E48\u4EC0\u4E48\u4FDD\u62A4\u624B\u6BB5\u90FD\u5C06\u662F\u65E0\u6548\u7684. \u4E0D\u8FC7\u5E78\u597D,ACE\u662F\u4E00\u79CD\u975E\u5E38\u4E25\u91CD\u7684\u5B89\u5168\u6F0F\u6D1E,\u51FA\u73B0\u7684\u6982\u7387\u76F8\u5BF9\u662F\u975E\u5E38\u4F4E\u7684.</p><p>\u53C2\u8003\u6587\u6863: <a href="https://blog.oversecured.com/Use-cryptography-in-mobile-apps-the-right-way/" target="_blank" rel="noopener noreferrer">Use cryptography in mobile apps the right way</a></p>`,25),o=[t];function c(l,r,u,i,k,d){return s(),a("div",null,o)}var h=n(p,[["render",c]]);export{y as __pageData,h as default};
